import React, { Component } from "react";
import { Card, Button } from "semantic-ui-react";

class CheckedIn extends Component {
  state = {};
  render() {
    const { appointments, pets, services } = this.props;
    console.log("appointment", appointments);
    console.log("pet", pets);

    const data = [];

    if (appointments.length > 0 && pets.length > 0)
      pets.forEach((pet) => {
        appointments.forEach((a) => {
          services.forEach((s) => {
            if (
              a.pet_id === pet.id &&
              a.service_id === s.id &&
              a.finish === null
            ) {
              data.push({
                appointmentId: a.id,
                petName: pet.name,
                petId: a.pet_id,
                service: s.name,
              });
            }
          });
        });
      });

    console.log("dataaaaaa", data);
    return (
      <Card.Group>
        {data.length > 0 ? (
          data.map((d) => (
            <Card>
              <Card.Content>
                <Card.Header>Name: {d.petName}</Card.Header>
                <Card.Meta>pet</Card.Meta>
                <Card.Description>Service: {d.service}</Card.Description>
                <Button
                  basic
                  color="pink"
                  onClick={() => this.props.finishOnClick(d)}
                >
                  Finish
                </Button>
              </Card.Content>
            </Card>
          ))
        ) : (
          <h1> No one is Checked In</h1>
        )}
      </Card.Group>
    );
  }
}
export default CheckedIn;
