import React, { Component } from "react";
import { Card, Button } from "semantic-ui-react";

class History extends Component {
  render() {
    const { pets, services } = this.props;
    const data = [];

    if (pets.length > 0) {
      pets.map((pet) => {
        console.log(pet.appointments.date);
        if (pet.appointments.length > 0) {
          pet.appointments.map((a) => {
            data.push({
              appointments: {
                service: a.service_id,
                date: a.date,
              },
            });
            return console.log("date", a);
          });
        }
      });
    }

    console.log(data);
    return (
      <Card.Group>
        {this.props.pets.length > 0 ? (
          this.props.pets.map((pet) => (
            <Card>
              <Card.Content>
                <Card.Header>{pet.name}</Card.Header>
                <Card.Meta>Pet</Card.Meta>
              </Card.Content>
              {/* <Card.Content extra>
                <div className="ui two buttons">
                  <Button basic color="green">
                    Approve
                  </Button>
                  <Button basic color="red">
                    Decline
                  </Button>
                </div>
              </Card.Content> */}
            </Card>
          ))
        ) : (
          <Card>
            <Card.Content>
              <Card.Header>No Pets</Card.Header>
            </Card.Content>
          </Card>
        )}
      </Card.Group>
    );
  }
}

export default History;

